import React, { useState, useMemo } from 'react';
import useProfileStore from '../../stores/profileStore';
import useNicheStore from '../../stores/nicheStore';
import Table from '../../components/common/Table';
import Button from '../../components/common/Button';
import './ProfileList.scss';

function ProfileList() {
  const {
    deleteProfiles,
    setFilters,
    getFilteredProfiles
  } = useProfileStore();

  const { selectedNicheId } = useNicheStore();

  // Initialize with sample data
  React.useEffect(() => {
    const now = new Date();
    const yesterday = new Date(now);
    yesterday.setDate(yesterday.getDate() - 1);
    const lastWeek = new Date(now);
    lastWeek.setDate(lastWeek.getDate() - 7);

    const generateProfiles = (nicheId, prefix, count) => {
      return Array.from({ length: count }, (_, i) => ({
        id: nicheId * 1000 + i,
        username: `${prefix}_${i + 1}`,
        status: Math.random() > 0.2 ? 'active' : 'inactive',
        lastChecked: [now, yesterday, lastWeek][Math.floor(Math.random() * 3)].toISOString(),
        lastDetected: Math.random() > 0.3 ? [now, yesterday, lastWeek][Math.floor(Math.random() * 3)].toISOString() : null,
        totalChecks: Math.floor(Math.random() * 200) + 50,
        totalDetections: Math.floor(Math.random() * 150),
        nicheId
      }));
    };

    useProfileStore.setState({
      profiles: [
        ...generateProfiles(1, 'fitness', 50),
        ...generateProfiles(2, 'fashion', 50),
        ...generateProfiles(3, 'food', 50)
      ]
    });
  }, []);

  const [selectedIds, setSelectedIds] = useState(new Set());
  const [lastSelectedId, setLastSelectedId] = useState(null);
  const [isSelecting, setIsSelecting] = useState(false);
  const [statusFilter, setStatusFilter] = useState('all');
  const [sortColumn, setSortColumn] = useState(null);
  const [sortDirection, setSortDirection] = useState(null);

  // Update filters when niche selection changes
  React.useEffect(() => {
    setFilters({ nicheId: selectedNicheId });
  }, [selectedNicheId, setFilters]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const filteredProfiles = useMemo(() => {
    const profiles = getFilteredProfiles();
    if (statusFilter === 'all') return profiles;
    return profiles.filter(profile => profile.status === statusFilter);
  }, [getFilteredProfiles, statusFilter]);

  const handleSort = (key, direction) => {
    setSortColumn(key);
    setSortDirection(direction);
  };

  const handleSelectAll = (checked) => {
    if (checked) {
      setSelectedIds(new Set(filteredProfiles.map(p => p.id)));
    } else {
      setSelectedIds(new Set());
    }
    setLastSelectedId(null);
  };

  const handleSelectOne = (id, checked, event) => {
    event.preventDefault(); // Prevent text selection
    
    const newSelected = new Set(selectedIds);
    
    if (event.shiftKey && lastSelectedId) {
      const profileIds = filteredProfiles.map(p => p.id);
      const currentIndex = profileIds.indexOf(id);
      const lastIndex = profileIds.indexOf(lastSelectedId);
      const [start, end] = [Math.min(currentIndex, lastIndex), Math.max(currentIndex, lastIndex)];
      
      for (let i = start; i <= end; i++) {
        if (checked) {
          newSelected.add(profileIds[i]);
        } else {
          newSelected.delete(profileIds[i]);
        }
      }
    } else {
      if (checked) {
        newSelected.add(id);
      } else {
        newSelected.delete(id);
      }
    }
    
    setSelectedIds(newSelected);
    setLastSelectedId(id);
  };

  const handleMouseDown = (id) => {
    setIsSelecting(true);
    setLastSelectedId(id);
    const newSelected = new Set(selectedIds);
    newSelected.add(id);
    setSelectedIds(newSelected);
  };

  const handleMouseEnter = (id) => {
    if (!isSelecting) return;
    
    const newSelected = new Set(selectedIds);
    const profileIds = filteredProfiles.map(p => p.id);
    const currentIndex = profileIds.indexOf(id);
    const lastIndex = profileIds.indexOf(lastSelectedId);
    const [start, end] = [Math.min(currentIndex, lastIndex), Math.max(currentIndex, lastIndex)];
    
    for (let i = start; i <= end; i++) {
      newSelected.add(profileIds[i]);
    }
    setSelectedIds(newSelected);
  };

  const handleMouseUp = () => {
    setIsSelecting(false);
  };

  React.useEffect(() => {
    document.addEventListener('mouseup', handleMouseUp);
    return () => document.removeEventListener('mouseup', handleMouseUp);
  }, []);

  const handleDelete = (id) => {
    deleteProfiles([id]);
  };

  const handleBulkDelete = () => {
    if (selectedIds.size > 0) {
      deleteProfiles(Array.from(selectedIds));
      setSelectedIds(new Set());
      setLastSelectedId(null);
    }
  };

  const columns = [
    {
      key: 'select',
      title: (
        <input
          type="checkbox"
          data-testid="select-all"
          checked={selectedIds.size === filteredProfiles.length}
          onChange={(e) => handleSelectAll(e.target.checked)}
        />
      ),
      render: (profile) => (
        <input
          type="checkbox"
          data-testid="profile-checkbox"
          checked={selectedIds.has(profile.id)}
          onMouseDown={() => handleMouseDown(profile.id)}
          onMouseEnter={() => handleMouseEnter(profile.id)}
          onChange={(e) => handleSelectOne(profile.id, e.target.checked, e)}
        />
      )
    },
    {
      key: 'url',
      title: 'URL',
      sortable: true,
      render: (profile) => (
        <a 
          href={`https://instagram.com/${profile.username}`}
          target="_blank"
          rel="noopener noreferrer"
        >
          {profile.username}
        </a>
      )
    },
    {
      key: 'username',
      title: 'Username',
      sortable: true
    },
    {
      key: 'lastChecked',
      title: 'Last Check',
      sortable: true,
      render: (profile) => formatDate(profile.lastChecked)
    },
    {
      key: 'lastDetected',
      title: 'Last Story',
      sortable: true,
      render: (profile) => profile.lastDetected ? formatDate(profile.lastDetected) : '-'
    },
    {
      key: 'totalChecks',
      title: 'Checks',
      sortable: true,
      render: (profile) => profile.totalChecks || 0
    },
    {
      key: 'totalDetections',
      title: 'Stories',
      sortable: true,
      render: (profile) => profile.totalDetections || 0
    },
    {
      key: 'actions',
      title: '',
      render: (profile) => (
        <Button
          variant="danger"
          size="small"
          onClick={() => handleDelete(profile.id)}
          data-testid="delete-button"
        >
          X
        </Button>
      )
    }
  ];

  return (
    <div className="profile-list">
      <div className="profile-list__header">
        <select
          className="profile-list__filter"
          data-testid="status-filter"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="all">All Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        {selectedIds.size > 0 && (
          <Button
            variant="danger"
            onClick={handleBulkDelete}
            data-testid="bulk-delete-button"
          >
            Delete ({selectedIds.size})
          </Button>
        )}
      </div>

      {!filteredProfiles.length ? (
        <div className="profile-list__empty">
          <p>No profiles found</p>
        </div>
      ) : (
        <Table
          data={filteredProfiles}
          columns={columns}
          onSort={handleSort}
          sortColumn={sortColumn}
          sortDirection={sortDirection}
          pageSize={100}
        />
      )}
    </div>
  );
}

export default ProfileList;
