import React from 'react';
import PropTypes from 'prop-types';
import TabNav from './TabNav';
import './AppLayout.scss';

const AppLayout = ({ activeTab, onTabChange, children }) => {
  const tabs = [
    { id: 'niche', label: 'Niche Feed' },
    { id: 'batch', label: 'Batch + Results' },
    { id: 'settings', label: 'Settings' }
  ];

  return (
    <div className="app-layout">
      <header className="app-layout__header">
        <h1 className="app-layout__title">Instagram Story Checker</h1>
        <TabNav
          tabs={tabs}
          activeTab={activeTab}
          onTabChange={onTabChange}
        />
      </header>
      <main className="app-layout__content">
        {children}
      </main>
    </div>
  );
};

AppLayout.propTypes = {
  /** Currently active tab ID */
  activeTab: PropTypes.oneOf(['niche', 'batch', 'settings']).isRequired,
  /** Tab change handler */
  onTabChange: PropTypes.func.isRequired,
  /** Tab content */
  children: PropTypes.node.isRequired
};

export default AppLayout;
