import React, { useState, useMemo } from 'react';
import Table from '../common/Table';
import Modal from '../common/Modal';
import './MasterList.scss';

const MasterList = ({
  profiles,
  onUpdateProfile,
  onDeleteProfile,
  onBulkDelete
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedProfiles, setSelectedProfiles] = useState([]);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [profileToDelete, setProfileToDelete] = useState(null);
  const [pageSize, setPageSize] = useState(10);

  const filteredProfiles = useMemo(() => {
    return profiles.filter(profile => {
      const matchesSearch = profile.username.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStatus = statusFilter === 'all' || profile.status === statusFilter;
      return matchesSearch && matchesStatus;
    });
  }, [profiles, searchTerm, statusFilter]);

  const handleStatusToggle = (profile) => {
    onUpdateProfile({
      ...profile,
      status: profile.status === 'active' ? 'inactive' : 'active'
    });
  };

  const handleDeleteClick = (profile) => {
    setProfileToDelete(profile);
    setShowDeleteConfirm(true);
  };

  const handleConfirmDelete = () => {
    if (profileToDelete) {
      onDeleteProfile(profileToDelete.id);
    } else if (selectedProfiles.length > 0) {
      onBulkDelete(selectedProfiles);
    }
    setShowDeleteConfirm(false);
    setProfileToDelete(null);
    setSelectedProfiles([]);
  };

  const handleBulkDeleteClick = () => {
    setProfileToDelete(null);
    setShowDeleteConfirm(true);
  };

  const columns = [
    {
      key: 'username',
      title: 'Username',
      sortable: true,
      render: (item) => item.username
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true,
      render: (item) => (
        <div>
          <span data-testid={`status-${item.id}`}>{item.status}</span>
          <button
            className={`status-toggle status-toggle--${item.status}`}
            onClick={() => handleStatusToggle(item)}
            aria-label="toggle status"
          >
            Toggle
          </button>
        </div>
      )
    },
    {
      key: 'lastChecked',
      title: 'Last Checked',
      sortable: true,
      render: (item) => new Date(item.lastChecked).toLocaleTimeString()
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (item) => (
        <button
          className="delete-button"
          onClick={() => handleDeleteClick(item)}
          aria-label="delete"
        >
          Delete
        </button>
      )
    }
  ];

  return (
    <div className="master-list">
      <div className="master-list__controls">
        <input
          type="text"
          placeholder="Search profiles..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="master-list__search"
        />
        
        <select
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          className="master-list__filter"
          aria-label="status filter"
        >
          <option value="all">All Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>

        <select
          value={pageSize}
          onChange={(e) => setPageSize(Number(e.target.value))}
          className="master-list__page-size"
          aria-label="items per page"
        >
          <option value={10}>10 per page</option>
          <option value={25}>25 per page</option>
          <option value={50}>50 per page</option>
        </select>

        {selectedProfiles.length > 0 && (
          <button
            onClick={handleBulkDeleteClick}
            className="master-list__bulk-delete"
            aria-label="delete selected"
          >
            Delete Selected ({selectedProfiles.length})
          </button>
        )}
      </div>

      {filteredProfiles.length > 0 ? (
        <Table
          data={filteredProfiles}
          columns={columns}
          pageSize={pageSize}
          selectable
          selectedRows={selectedProfiles}
          onSelectionChange={setSelectedProfiles}
        />
      ) : (
        <div className="master-list__empty">
          No profiles found
        </div>
      )}

      <Modal
        title="Confirm Delete"
        isOpen={showDeleteConfirm}
        onClose={() => {
          setShowDeleteConfirm(false);
          setProfileToDelete(null);
        }}
      >
        <div className="master-list__confirm">
          <h3>Confirm Delete</h3>
          <p>
            {profileToDelete
              ? `Are you sure you want to delete ${profileToDelete.username}?`
              : `Are you sure you want to delete ${selectedProfiles.length} selected profiles?`}
          </p>
          <div className="master-list__confirm-actions">
            <button
              onClick={() => setShowDeleteConfirm(false)}
              className="master-list__confirm-cancel"
            >
              Cancel
            </button>
            <button
              onClick={handleConfirmDelete}
              className="master-list__confirm-delete"
              aria-label="confirm"
            >
              Delete
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default MasterList;
