import React, { useState, useMemo } from 'react';
import useProfileStore from '../../stores/profileStore';
import useNicheStore from '../../stores/nicheStore';
import Table from '../common/Table';
import './MasterList.scss';

function MasterList() {
  const { profiles } = useProfileStore();
  const { niches } = useNicheStore();
  const [statusFilter, setStatusFilter] = useState('all');

  const getNicheName = (nicheId) => {
    const niche = niches.find(n => n.id === nicheId);
    return niche ? niche.name : '-';
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const filteredProfiles = useMemo(() => {
    let result = [...profiles];
    
    if (statusFilter !== 'all') {
      result = result.filter(profile => profile.status === statusFilter);
    }

    // Sort alphabetically by username
    return result.sort((a, b) => a.username.localeCompare(b.username));
  }, [profiles, statusFilter]);

  const columns = [
    {
      key: 'username',
      title: 'Username',
      sortable: true,
      render: (profile) => (
        <a 
          href={`https://instagram.com/${profile.username}`}
          target="_blank"
          rel="noopener noreferrer"
        >
          {profile.username}
        </a>
      )
    },
    {
      key: 'niche',
      title: 'Niche',
      sortable: true,
      render: (profile) => getNicheName(profile.nicheId)
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true
    },
    {
      key: 'lastChecked',
      title: 'Last Updated',
      sortable: true,
      render: (profile) => formatDate(profile.lastChecked)
    }
  ];

  return (
    <div className="master-list">
      <div className="master-list__header">
        <select
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          className="master-list__filter"
        >
          <option value="all">All Status</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
      </div>

      <Table
        data={filteredProfiles}
        columns={columns}
        pageSize={500}
      />
    </div>
  );
}

export default MasterList;
