import React, { useState } from 'react';
import useNicheStore from '../../stores/nicheStore';
import Button from '../common/Button';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './NicheFeed.scss';

function NicheFeed() {
  const {
    niches,
    selectedNicheId,
    setSelectedNicheId,
    deleteNiche,
    updateNiche,
    addNiche,
    reorderNiches
  } = useNicheStore();

  const [isAdding, setIsAdding] = useState(false);
  const [newNicheName, setNewNicheName] = useState('');

  const handleNicheClick = (id) => {
    setSelectedNicheId(id === selectedNicheId ? null : id);
  };

  const handleDelete = (e, id) => {
    e.stopPropagation();
    deleteNiche(id);
  };

  const handleNameChange = (id, newName) => {
    updateNiche(id, { name: newName });
  };

  const handleAddClick = () => {
    setIsAdding(true);
  };

  const handleAddSubmit = (e) => {
    e.preventDefault();
    if (newNicheName.trim()) {
      addNiche({ name: newNicheName.trim() });
      setNewNicheName('');
      setIsAdding(false);
    }
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;

    if (sourceIndex === destinationIndex) return;

    reorderNiches(sourceIndex, destinationIndex);
  };

  return (
    <div className="niche-feed">
      <div className="niche-feed__header">
        <Button onClick={handleAddClick} data-testid="add-niche-button">
          Add Niche
        </Button>
      </div>

      {isAdding && (
        <form onSubmit={handleAddSubmit} className="niche-feed__add-form">
          <input
            type="text"
            value={newNicheName}
            onChange={(e) => setNewNicheName(e.target.value)}
            placeholder="Enter niche name"
            autoFocus
            className="niche-feed__input"
          />
          <Button type="submit">Save</Button>
          <Button variant="secondary" onClick={() => setIsAdding(false)}>
            Cancel
          </Button>
        </form>
      )}

      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="niches">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className="niche-feed__list"
            >
              {niches.map((niche, index) => (
                <Draggable
                  key={niche.id}
                  draggableId={niche.id.toString()}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className={`niche-feed__item ${
                        selectedNicheId === niche.id ? 'niche-feed__item--selected' : ''
                      } ${snapshot.isDragging ? 'niche-feed__item--dragging' : ''}`}
                      onClick={() => handleNicheClick(niche.id)}
                      data-testid="niche-item"
                    >
                      <div className="niche-feed__content">
                        <input
                          type="text"
                          value={niche.name}
                          onChange={(e) => handleNameChange(niche.id, e.target.value)}
                          onClick={(e) => e.stopPropagation()}
                          className="niche-feed__name"
                          data-testid="niche-name-input"
                        />
                        <span className="niche-feed__counter">
                          {niche.profileCount || 0} profiles
                        </span>
                      </div>
                      <Button
                        variant="danger"
                        size="small"
                        onClick={(e) => handleDelete(e, niche.id)}
                        data-testid="delete-button"
                      >
                        X
                      </Button>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default NicheFeed;
