import React, { useState } from 'react';
import useBatchStore from '../../stores/batchStore';
import useProfileStore from '../../stores/profileStore';
import BatchControl from './BatchControl';
import ResultsDisplay from './ResultsDisplay';
import BatchProgress from './BatchProgress';
import './BatchResults.scss';

const BatchResults = () => {
  const { getFilteredProfiles } = useProfileStore();
  const { loading: isRunning, error, createBatch, cancelBatch } = useBatchStore();
  const selectedProfiles = getFilteredProfiles().filter(p => p.selected);
  return (
    <div className="batch-results">
      <div className="batch-results__controls">
        <BatchControl 
          onStart={() => createBatch()}
          onCancel={() => cancelBatch()}
          isRunning={isRunning}
          selectedProfiles={selectedProfiles}
          error={error}
        />
        <BatchProgress />
      </div>
      <div className="batch-results__display">
        <ResultsDisplay />
      </div>
    </div>
  );
};

export default BatchResults;
