import React, { useState } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import './NicheList.scss';

const NicheList = ({
  niches,
  selectedNicheId,
  onSelect,
  onReorder
}) => {
  const [dragIndex, setDragIndex] = useState(null);
  const [overIndex, setOverIndex] = useState(null);

  const handleDragStart = (index) => {
    setDragIndex(index);
  };

  const handleDragOver = (e, index) => {
    e.preventDefault();
    if (index !== dragIndex) {
      setOverIndex(index);
    }
  };

  const handleDragEnd = () => {
    setDragIndex(null);
    setOverIndex(null);
  };

  const handleDrop = (index) => {
    if (dragIndex !== null && dragIndex !== index) {
      onReorder(dragIndex, index);
    }
    handleDragEnd();
  };

  const handleKeyDown = (e, id) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      onSelect(id);
    }
  };

  if (!niches.length) {
    return (
      <div className="niche-list niche-list--empty">
        <p>No niches found</p>
      </div>
    );
  }

  return (
    <ul className="niche-list">
      {niches.map((niche, index) => (
        <li
          key={niche.id}
          role="listitem"
          tabIndex={0}
          className={classNames('niche-list__item', {
            'niche-list__item--selected': niche.id === selectedNicheId,
            'niche-list__item--dragging': index === dragIndex,
            'niche-list__item--over': index === overIndex
          })}
          onClick={() => onSelect(niche.id)}
          onKeyDown={(e) => handleKeyDown(e, niche.id)}
          draggable
          onDragStart={() => handleDragStart(index)}
          onDragOver={(e) => handleDragOver(e, index)}
          onDragEnd={handleDragEnd}
          onDrop={() => handleDrop(index)}
        >
          <div className="niche-list__item-content">
            <span className="niche-list__item-name">{niche.name}</span>
            <span className="niche-list__item-count">{niche.profileCount}</span>
          </div>
        </li>
      ))}
    </ul>
  );
};

NicheList.propTypes = {
  /** Array of niche objects */
  niches: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    profileCount: PropTypes.number.isRequired
  })).isRequired,
  /** ID of the currently selected niche */
  selectedNicheId: PropTypes.number,
  /** Selection change handler */
  onSelect: PropTypes.func.isRequired,
  /** Reorder handler */
  onReorder: PropTypes.func.isRequired
};

export default NicheList;
