import React, { useState, useRef, useCallback } from 'react';
import PropTypes from 'prop-types';
import Button from '../common/Button';
import Modal from '../common/Modal';
import './FileImporter.scss';

/**
 * @typedef {Object} FileImporterProps
 * @property {Function} onImport - Callback function when file is imported
 * @property {string[]} allowedTypes - Array of allowed file extensions
 * @property {number} maxSize - Maximum file size in bytes
 */

/**
 * FileImporter component for handling file uploads with drag & drop support
 * @param {FileImporterProps} props
 */
const FileImporter = ({ onImport, allowedTypes, maxSize }) => {
  const [isDragActive, setIsDragActive] = useState(false);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const fileInputRef = useRef(null);

  const validateFile = (file) => {
    const extension = '.' + file.name.split('.').pop().toLowerCase();
    
    if (!allowedTypes.includes(extension)) {
      throw new Error('Invalid file type. Allowed types: ' + allowedTypes.join(', '));
    }
    
    if (file.size > maxSize) {
      throw new Error('File too large. Maximum size: ' + (maxSize / (1024 * 1024)) + 'MB');
    }
  };

  const handleFile = async (file) => {
    try {
      setError('');
      setIsLoading(true);
      validateFile(file);
      await onImport(file);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    setIsDragActive(false);
    
    const file = e.dataTransfer.files[0];
    if (file) {
      handleFile(file);
    }
  }, []);

  const handleDragEnter = useCallback((e) => {
    e.preventDefault();
    setIsDragActive(true);
  }, []);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    setIsDragActive(false);
  }, []);

  const handleInputChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      handleFile(file);
    }
  };

  const handleClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="file-importer">
      <div
        className={`file-importer__drop-zone ${isDragActive ? 'file-importer__drop-zone--active' : ''} ${error ? 'file-importer__drop-zone--error' : ''}`}
        data-testid="drop-zone"
        onDrop={handleDrop}
        onDragEnter={handleDragEnter}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onClick={handleClick}
      >
        <input
          ref={fileInputRef}
          type="file"
          className="file-importer__input"
          data-testid="file-input"
          accept={allowedTypes.join(',')}
          onChange={handleInputChange}
        />
        
        <div className="file-importer__icon">
          üìÅ
        </div>
        
        <p className="file-importer__text">
          {isDragActive ? 'Drop file here' : 'Drag and drop file here'}
          <br />
          or click to select
        </p>
        
        <p className="file-importer__allowed-types">
          Allowed types: {allowedTypes.join(', ')}
        </p>
      </div>

      {isLoading && (
        <div className="file-importer__progress" role="progressbar">
          Importing...
        </div>
      )}

      {error && (
        <p className="file-importer__error">
          {error}
        </p>
      )}
    </div>
  );
};

FileImporter.propTypes = {
  onImport: PropTypes.func.isRequired,
  allowedTypes: PropTypes.arrayOf(PropTypes.string).isRequired,
  maxSize: PropTypes.number.isRequired
};

export default FileImporter;
