import React, { useState } from 'react';
import useProxyStore from '../../stores/proxyStore';
import Button from '../common/Button';
import './ProxyManager.scss';

function ProxyManager() {
  const { 
    proxies,
    addProxy,
    removeProxy,
    testProxy
  } = useProxyStore();

  const [proxyInput, setProxyInput] = useState('');

  const handleAddProxy = () => {
    if (!proxyInput.trim()) return;

    const [ip, port, user, pass] = proxyInput.split(':');
    if (!ip || !port || !user || !pass) {
      alert('Invalid proxy format. Use: ip:port:user:pass');
      return;
    }

    addProxy({
      host: ip,
      port,
      username: user,
      password: pass,
      status: 'untested'
    });
    setProxyInput('');
  };

  const handleRemoveProxy = (index) => {
    removeProxy(index);
  };

  const handleTestProxy = async (index) => {
    await testProxy(index);
  };

  return (
    <div className="proxy-manager">
      <div className="proxy-manager__header">
        <h2>Proxy Management</h2>
      </div>

      <div className="proxy-manager__add-form">
        <input
          type="text"
          value={proxyInput}
          onChange={(e) => setProxyInput(e.target.value)}
          placeholder="ip:port:user:pass"
          className="proxy-manager__input"
        />
        <Button onClick={handleAddProxy}>Add Proxy</Button>
      </div>

      <div className="proxy-manager__list">
        {proxies.map((proxy, index) => (
          <div key={index} className="proxy-manager__item">
            <div className="proxy-manager__proxy-text">
              {`${proxy.host}:${proxy.port}:${proxy.username}:${proxy.password}`}
            </div>
            <div className="proxy-manager__actions">
              <span className={`proxy-manager__status proxy-manager__status--${proxy.status}`}>
                {proxy.status}
              </span>
              <Button onClick={() => handleTestProxy(index)} size="small">
                Test
              </Button>
              <Button 
                onClick={() => handleRemoveProxy(index)} 
                variant="danger"
                size="small"
              >
                Remove
              </Button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProxyManager;
