import React, { useEffect, useCallback } from 'react';
import useNicheStore from '../../stores/nicheStore';
import useProfileStore from '../../stores/profileStore';
import NicheList from './NicheList';
import ProfileList from './ProfileList';
import FileImporter from './FileImporter';
import './NicheFeed.scss';

function NicheFeed() {
  const {
    niches,
    selectedNicheId,
    setNiches,
    setSelectedNicheId,
    reorderNiches,
    getSortedNiches
  } = useNicheStore();

  // For development, initialize with some sample data
  useEffect(() => {
    if (niches.length === 0) {
      setNiches([
        { id: 1, name: 'Fitness', profileCount: 5, order: 1 },
        { id: 2, name: 'Fashion', profileCount: 3, order: 2 },
        { id: 3, name: 'Food', profileCount: 4, order: 3 }
      ]);
    }
  }, [niches.length, setNiches]);

  const handleNicheSelect = (id) => {
    setSelectedNicheId(id);
  };

  const handleNicheReorder = (fromIndex, toIndex) => {
    reorderNiches(fromIndex, toIndex);
  };

  return (
    <div className="niche-feed">
      <div className="niche-feed__left-panel">
        <h2 className="niche-feed__panel-title">Niches</h2>
        <NicheList
          niches={getSortedNiches()}
          selectedNicheId={selectedNicheId}
          onSelect={handleNicheSelect}
          onReorder={handleNicheReorder}
        />
      </div>
      <div className="niche-feed__right-panel">
        <div className="niche-feed__actions">
          <h2 className="niche-feed__panel-title">Profiles</h2>
          <FileImporter 
            onImport={async (file) => {
              const text = await file.text();
              const lines = text.split('\n');
              const headers = lines[0].toLowerCase().split(',');
              
              const profiles = lines.slice(1)
                .filter(line => line.trim())
                .map(line => {
                  const values = line.split(',');
                  const profile = {};
                  headers.forEach((header, index) => {
                    profile[header.trim()] = values[index]?.trim() || '';
                  });
                  return {
                    ...profile,
                    id: Math.random().toString(36).substr(2, 9),
                    status: 'active',
                    nicheId: selectedNicheId
                  };
                });
              
              useProfileStore.getState().addProfiles(profiles);
            }}
            allowedTypes={['.csv', '.txt']}
            maxSize={5 * 1024 * 1024} // 5MB limit
          />
        </div>
        <ProfileList />
      </div>
    </div>
  );
}

export default NicheFeed;
