import React from 'react';
import useProxyStore from '../../stores/proxyStore';
import Button from '../common/Button';
import './ProxyManager.scss';

function ProxyManager() {
  const { 
    proxies,
    addProxy,
    removeProxy,
    updateProxy,
    testProxy
  } = useProxyStore();

  const handleAddProxy = () => {
    addProxy();
  };

  const handleRemoveProxy = (index) => {
    removeProxy(index);
  };

  const handleUpdateProxy = (index, field, value) => {
    updateProxy(index, { [field]: value });
  };

  const handleTestProxy = async (index) => {
    await testProxy(index);
  };

  return (
    <div className="proxy-manager">
      <div className="proxy-manager__header">
        <h2>Proxy Management</h2>
        <Button onClick={handleAddProxy}>Add Proxy</Button>
      </div>

      <div className="proxy-manager__list">
        {proxies.map((proxy, index) => (
          <div key={index} className="proxy-manager__item">
            <div className="proxy-manager__fields">
              <input
                type="text"
                value={proxy.host}
                onChange={(e) => handleUpdateProxy(index, 'host', e.target.value)}
                placeholder="Host"
                className="proxy-manager__input"
              />
              <input
                type="text"
                value={proxy.port}
                onChange={(e) => handleUpdateProxy(index, 'port', e.target.value)}
                placeholder="Port"
                className="proxy-manager__input"
              />
              <input
                type="text"
                value={proxy.username}
                onChange={(e) => handleUpdateProxy(index, 'username', e.target.value)}
                placeholder="Username"
                className="proxy-manager__input"
              />
              <input
                type="password"
                value={proxy.password}
                onChange={(e) => handleUpdateProxy(index, 'password', e.target.value)}
                placeholder="Password"
                className="proxy-manager__input"
              />
            </div>

            <div className="proxy-manager__actions">
              <span className={`proxy-manager__status proxy-manager__status--${proxy.status}`}>
                {proxy.status}
              </span>
              <Button onClick={() => handleTestProxy(index)} size="small">
                Test
              </Button>
              <Button 
                onClick={() => handleRemoveProxy(index)} 
                variant="danger"
                size="small"
              >
                Remove
              </Button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProxyManager;
