import React from 'react';
import useProfileStore from '../../stores/profileStore';
import useSettingsStore from '../../stores/settingsStore';
import MasterList from './MasterList';
import StoryTargets from './StoryTargets';
import ProxyManager from './ProxyManager';
import './Settings.scss';

const Settings = () => {
  const { profiles, updateProfile, deleteProfiles } = useProfileStore();
  return (
    <div className="settings">
      <div className="settings__section">
        <h2 className="settings__title">Master List</h2>
        <MasterList 
          profiles={profiles || []}
          onUpdateProfile={(profile) => updateProfile(profile.id, profile)}
          onDeleteProfile={(id) => deleteProfiles([id])}
          onBulkDelete={deleteProfiles}
        />
      </div>
      <div className="settings__section">
        <h2 className="settings__title">Story Targets</h2>
        <StoryTargets 
          targets={useSettingsStore(state => state.targets)}
          onUpdate={useSettingsStore(state => state.updateTargets)}
        />
      </div>
      <div className="settings__section">
        <h2 className="settings__title">Proxy Management</h2>
        <ProxyManager />
      </div>
    </div>
  );
};

export default Settings;
