import React from 'react';
import { observer } from 'mobx-react-lite';
import { nicheStore } from '../../stores/nicheStore';
import NicheList from './NicheList';
import ProfileList from './ProfileList';
import FileImporter from './FileImporter';
import './NicheFeed.scss';

const NicheFeed = observer(() => {
  // For development, initialize with some sample data
  React.useEffect(() => {
    if (nicheStore.niches.length === 0) {
      nicheStore.setNiches([
        { id: 1, name: 'Fitness', profileCount: 5, order: 1 },
        { id: 2, name: 'Fashion', profileCount: 3, order: 2 },
        { id: 3, name: 'Food', profileCount: 4, order: 3 }
      ]);
    }
  }, []);

  const handleNicheSelect = (id) => {
    nicheStore.setSelectedNicheId(id);
  };

  const handleNicheReorder = (fromIndex, toIndex) => {
    // For development, just reorder locally
    const reorderedNiches = [...nicheStore.niches];
    const [movedNiche] = reorderedNiches.splice(fromIndex, 1);
    reorderedNiches.splice(toIndex, 0, movedNiche);
    nicheStore.setNiches(reorderedNiches);
  };

  return (
    <div className="niche-feed">
      <div className="niche-feed__left-panel">
        <h2 className="niche-feed__panel-title">Niches</h2>
        <NicheList
          niches={nicheStore.sortedNiches}
          selectedNicheId={nicheStore.selectedNicheId}
          onSelect={handleNicheSelect}
          onReorder={handleNicheReorder}
        />
      </div>
      <div className="niche-feed__right-panel">
        <div className="niche-feed__actions">
          <h2 className="niche-feed__panel-title">Profiles</h2>
          <FileImporter />
        </div>
        <ProfileList />
      </div>
    </div>
  );
});

export default NicheFeed;
